import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
	const [day, setDay] = useState(null);
	const [hour, setHour] = useState(null);
	const [minute, setMinute] = useState(null);
	const [second, setSecond] = useState(null);

	useEffect(() => {
		const interval = setInterval(() => {
			let christmas = new Date('Dec 26, 2020').getTime();
			let today = new Date().getTime();
			let timeBetween = christmas - today;

			setDay(Math.floor(timeBetween / (1000 * 60 * 60 * 24)));
			setHour(
				Math.floor((timeBetween % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
			);
			setMinute(Math.floor((timeBetween % (1000 * 60 * 60)) / (1000 * 60)));
			setSecond(Math.floor(timeBetween % (1000 * 60) / 1000));
      if(timeBetween < 0) {
        clearInterval(interval)
      }
    }, 1000);
    return () => clearInterval(interval)
	});

	return (
		<div className='App'>
			<p>Christmas</p>
			<div className='App__time'>
				<>{day}</>
				<h1>{hour}</h1>
				<h1>{minute}</h1>
				<h1>{second}</h1>
			</div>
			<div className='App__labels'>
				<h5>days</h5>
				<h5>hours</h5>
				<h5>mins</h5>
				<h5>secs</h5>
			</div>
		</div>
	);
}

export default App;
