import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [day, setDay] = useState(null)
  const [hour, setHour] = useState(null)
  const [minute, setMinute] = useState(null)
  const [second, setSecond] = useState(null)

  useEffect(() => {
    let christmas = new Date('Dec 26, 2020').getTime();
    let today = new Date().getTime();

    setDay(Math.floor(christmas - today / (1000 * 60 * 60 * 24))) 
    setHour(Math.floor(christmas - today / (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
    setMinute(Math.floor(christmas - today / (1000 * 60 * 60 * 24)) / (1000 * 60)) 
    setSecond(Math.floor(christmas - today / (1000 * 60 * 60 * 24)) / 1000) 

  })

	return (
		<div className='App'>
			<p>Christmas</p>
			<div className='App__time'>
				<h1>{day}</h1>
  <h1>{hour}</h1>
				<h1>59</h1>
				<h1>59</h1>
			</div>
			<div className='App__labels'>
				<h5>days</h5>
				<h5>hours</h5>
				<h5>mins</h5>
				<h5>secs</h5>
			</div>
		</div>
	);
}

export default App;
